$f3->route('POST /api/thread/create',
	function($f3) {
        $board = $f3->get("POST.board");
        $subject = $f3->get("POST.subject");
        $comment = $f3->get("POST.comment");
        $user = $f3->get("POST.user");
        $ip = $_SERVER['REMOTE_ADDR'];
		createThread($board, $subject, $comment, $user, $ip);
	}
);


$f3->route('POST /api/post/create',
	function($f3) {     
        $form = getPostData();
        
        $ip = $_SERVER['REMOTE_ADDR'];
        
        $op = postFromNumber($form['board'], $form['threadnumber']);

        if(!dbExists($op)) {
            badRequest(array());
        }
        
        $threadId = $op[TTHREAD::TABLE][COMMONS::ID];
		createPost($threadId, $form['subject'], $form['comment'], $form['user'], $ip);
        
        restOK(array());
	}
);


$f3->route('GET /api/board/thread/list/@boardURL',
	function($f3) {
        $boardUrl = $f3->get("PARAMS.boardURL");
		$board = boardByURL($boardUrl);
        
        if(!dbExists($board)) {
            badRequest(array());
        }
        
        $response=array();
        $response['threads']=array();
        $threads = $board[TBOARD::THREADS];
        $i=0;
        foreach($threads as $thread) {
            $posts = $thread[TTHREAD::POSTS];
            $response['threads'][$i]=array();
            $response['threads'][$i]['posts'] = array();
            foreach($posts as $post) {
                $response['threads'][$i]['posts'][]=propertiesAsArray($post,
                            TPOST::POSTNUMBER,
                            TPOST::SUBJECT,
                            TPOST::COMMENT,
                            TPOST::USER,
                            TPOST::TIMESTAMP,
                            TPOST::ISOP
                );
            }
            $i++;
        }
        restOK($response);
	}
);

$f3->route('GET /api/thread/@boardUrl/@postNumber',
	function($f3) {
        $boardUrl = $f3->get("PARAMS.boardUrl");
        $postNumber = $f3->get("PARAMS.postNumber");
        
        $op = postFromNumber($boardUrl, $postNumber);
        
        if(!dbExists($op)) {
            badRequest(array());
        }
        
        $thread = $op[TTHREAD::TABLE];
        
        $response=array();
        $posts = $thread[TTHREAD::POSTS];
        $response['posts'] = array();
        foreach($posts as $post) {
            $response['posts'][]=propertiesAsArray($post,
                        TPOST::POSTNUMBER,
                        TPOST::SUBJECT,
                        TPOST::COMMENT,
                        TPOST::USER,
                        TPOST::TIMESTAMP,
                        TPOST::ISOP
            );
        }
        restOK($response);
	}
);

$f3->route('GET /api/board/list',
	function($f3) {
		$boards = R::findAll(TBOARD::TABLE);
        $response=array();
        foreach($boards as $board) {
            $response[]=propertiesAsArray($board,
                        TBOARD::URLNAME,
                        TBOARD::NAME,
                        TBOARD::DEFAULTPOSTER
            );
        }
        restOK($response);
	}
);

$f3->route('POST /api/board/create',
	function($f3) {
        $errors = array();
        
        $urlname = $f3->get("POST.urlname");
        $name = $f3->get("POST.name");
        $defaultposter = $f3->get("POST.defaultposter");
        
        if(!validateBoardName($urlname)) {
            $errors[]="api.board.create.invalidurl";
        }
        
        if(boardExists($urlname)) {
            $errors[]="api.board.create.alreadyexists";
        }
        
        if(!empty($errors)) {
            badRequest($errors);
        }
        
        createBoard($urlname, $name, $defaultposter);
	}
);

$f3->run();

function globInclude($str){
    foreach (glob($str) as $filename)
    {
        include $filename;
    }
}
